def get_lines(first, last, reverse=False):
    for i in range(first, last):
        if reverse:
            print(f"Line({i}) = {{{i+1}, {i}}};")
        else:
            print(f"Line({i}) = {{{i}, {i+1}}};")
    if reverse:
        print(f"Line({last}) = {{{first}, {last}}};")
    else:
        print(f"Line({last}) = {{{last}, {first}}};")

