def get_loop(num, start, end):
    print(f"Line Loop({num}) = {{{start}")
    for i in range(start+1, end+1):
        print(f", {i}")
    print("};")



def get_sequence(first, last):
    for i in range(first, last):
        print(f"{i}, ", end="")
    print(f"{last}")

